    private void jButtonRealizarCadastroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonRealizarCadastroActionPerformed
        try {
            if (controller.cadastrarProduto(jTextFieldNome.getText(), jTextFieldMarca.getText(), jFormattedTextFieldPreco.getText(), jTextFieldQtd.getText() )) {
                dispose(); 
            }
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Erro ao cadastrar o produto: " + ex.getMessage());
        }
    }//GEN-LAST:event_jButtonRealizarCadastroActionPerformed

